@testable import Chesterfield
import XCTest

class TextQuestionPresenterTests: XCTestCase
{
  // MARK: Subject under test
  
  var sut: TextQuestionPresenter!
  
  // MARK: Test lifecycle
  
  override func setUp()
  {
    super.setUp()
    setupTextQuestionPresenter()
  }
  
  override func tearDown()
  {
    super.tearDown()
  }
  
  // MARK: Test setup
  
  func setupTextQuestionPresenter()
  {
    sut = TextQuestionPresenter()
  }
  
  // MARK: Test doubles
  
  class TextQuestionDisplayLogicSpy: TextQuestionDisplayLogic
  {
    var displaySomethingCalled = false
    
    func displaySomething(viewModel: TextQuestion.Something.ViewModel)
    {
      displaySomethingCalled = true
    }
  }
  
  // MARK: Tests
  
  func testPresentSomething()
  {
    // Given
    let spy = TextQuestionDisplayLogicSpy()
    sut.viewController = spy
    let response = TextQuestion.Something.Response()
    
    // When
    sut.presentSomething(response: response)
    
    // Then
    XCTAssertTrue(spy.displaySomethingCalled, "presentSomething(response:) should ask the view controller to display the result")
  }
}
